import spacy
from collections import Counter
import math
import numpy as np

nlp = spacy.load("ru_core_news_lg")

def analyze_text(text):
    doc = nlp(text)

    # === –ë–ê–ó–û–í–´–ô –ê–ù–ê–õ–ò–ó –¢–û–ö–ï–ù–û–í ===
    tokens = [token.text for token in doc]
    words = [token.text for token in doc if token.is_alpha]
    unique_words = set(words)
    stop_words = [token.text for token in doc if token.is_stop]
    avg_word_length = sum(len(word) for word in words) / len(words) if words else 0

    # === –ú–û–†–§–û–õ–û–ì–ò–ß–ï–°–ö–ò–ô –ê–ù–ê–õ–ò–ó ===
    pos_counts = Counter([token.pos_ for token in doc if token.is_alpha])
    lemmas = [token.lemma_ for token in doc if token.is_alpha]
    unique_lemmas = set(lemmas)
    
    # === –°–ò–ù–¢–ê–ö–°–ò–ß–ï–°–ö–ò–ô –ê–ù–ê–õ–ò–ó ===
    dependencies = Counter([token.dep_ for token in doc if token.dep_ != ""])
    
    has_noun_chunks = False
    try:
        next(doc.noun_chunks, None)
        has_noun_chunks = True
    except NotImplementedError:
        pass
    
    # === –ò–ú–ï–ù–û–í–ê–ù–ù–´–ï –°–£–©–ù–û–°–¢–ò (NER) ===
    entities = [(ent.text, ent.label_) for ent in doc.ents]
    entity_counts = Counter([ent.label_ for ent in doc.ents])
    
    # === –õ–ï–ö–°–ò–ß–ï–°–ö–û–ï –†–ê–ó–ù–û–û–ë–†–ê–ó–ò–ï ===
    ttr = len(unique_words) / len(words) if words else 0
    
    def calculate_simplified_mtld(text_tokens, ttr_threshold=0.72):
        if len(text_tokens) < 10:
            return 0
        
        segments = []
        current_segment = []
        
        for token in text_tokens:
            current_segment.append(token)
            current_ttr = len(set(current_segment)) / len(current_segment)
            
            if current_ttr <= ttr_threshold and len(current_segment) >= 10:
                segments.append(current_segment)
                current_segment = []
        
        if current_segment:
            segments.append(current_segment)
            
        if not segments:
            return 0
        
        return len(text_tokens) / len(segments)
    
    mtld = calculate_simplified_mtld(words)
    
    # === –ß–ò–¢–ê–ë–ï–õ–¨–ù–û–°–¢–¨ –¢–ï–ö–°–¢–ê ===
    sentences = list(doc.sents)
    sentence_lengths = [len(sent) for sent in sentences]
    avg_sentence_length = sum(sentence_lengths) / len(sentences) if sentences else 0
    
    words_per_sentence = len(words) / len(sentences) if sentences else 0
    
    def count_syllables_ru(word):
        return len([c for c in word.lower() if c in '–∞–µ—ë–∏–æ—É—ã—ç—é—è'])
    
    syllables = sum(count_syllables_ru(word) for word in words)
    syllables_per_word = syllables / len(words) if words else 0
    flesh_kincaid = 206.835 - 1.3 * words_per_sentence - 60.1 * syllables_per_word
    
    # === –°–õ–û–ñ–ù–û–°–¢–¨ –¢–ï–ö–°–¢–ê ===
    long_words = [word for word in words if count_syllables_ru(word) > 4]
    long_words_percent = len(long_words) / len(words) * 100 if words else 0
    
    # === –ß–ê–°–¢–û–¢–ù–û–°–¢–¨ –°–õ–û–í ===
    word_freq = Counter(words)
    most_common_words = word_freq.most_common(10)
    
    # === –í–ï–ö–¢–û–†–ù–´–ï –•–ê–†–ê–ö–¢–ï–†–ò–°–¢–ò–ö–ò ===
    doc_vector = doc.vector
    vector_norm = np.linalg.norm(doc_vector)
    
    # === –ü–†–ï–î–õ–û–ñ–ï–ù–ò–Ø –ò –ò–• –•–ê–†–ê–ö–¢–ï–†–ò–°–¢–ò–ö–ò ===
    sentence_count = len(sentences)
    question_count = sum(1 for sent in sentences if sent.text.strip().endswith('?'))
    exclamation_count = sum(1 for sent in sentences if sent.text.strip().endswith('!'))
    
    # === –í–´–í–û–î –°–¢–ê–¢–ò–°–¢–ò–ö–ò ===
    print(f"\nüìä –ü–û–õ–ù–´–ô –ê–ù–ê–õ–ò–ó –¢–ï–ö–°–¢–ê")
    print(f"\n=== –ë–ê–ó–û–í–ê–Ø –°–¢–ê–¢–ò–°–¢–ò–ö–ê ===")
    print(f"- –í—Å–µ–≥–æ —Ç–æ–∫–µ–Ω–æ–≤: {len(tokens)}")
    print(f"- –í—Å–µ–≥–æ —Å–ª–æ–≤: {len(words)}")
    print(f"- –£–Ω–∏–∫–∞–ª—å–Ω—ã—Ö —Å–ª–æ–≤: {len(unique_words)}")
    print(f"- –°—Ç–æ–ø-—Å–ª–æ–≤: {len(stop_words)}")
    print(f"- –°—Ä–µ–¥–Ω—è—è –¥–ª–∏–Ω–∞ —Å–ª–æ–≤–∞: {avg_word_length:.2f} —Å–∏–º–≤–æ–ª–æ–≤")
    
    print(f"\n=== –ú–û–†–§–û–õ–û–ì–ò–ß–ï–°–ö–ò–ô –ê–ù–ê–õ–ò–ó ===")
    print(f"- –†–∞—Å–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ —á–∞—Å—Ç–µ–π —Ä–µ—á–∏:")
    for pos, count in pos_counts.most_common():
        print(f"  ‚Ä¢ {pos}: {count} ({count/len(words)*100:.1f}%)")
    print(f"- –£–Ω–∏–∫–∞–ª—å–Ω—ã—Ö –ª–µ–º–º: {len(unique_lemmas)}")
    print(f"- –õ–µ–º–º–∞—Ç–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω–æ–µ —Å–æ–æ—Ç–Ω–æ—à–µ–Ω–∏–µ: {len(unique_lemmas)/len(unique_words):.2f}")
    
    print(f"\n=== –°–ò–ù–¢–ê–ö–°–ò–ß–ï–°–ö–ò–ô –ê–ù–ê–õ–ò–ó ===")
    print(f"- –°–∏–Ω—Ç–∞–∫—Å–∏—á–µ—Å–∫–∏–µ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ (—Ç–æ–ø-5):")
    for dep, count in dependencies.most_common(5):
        print(f"  ‚Ä¢ {dep}: {count}")
    
    if has_noun_chunks:
        noun_chunks = list(doc.noun_chunks)
        print(f"- –ò–º–µ–Ω–Ω—ã—Ö –≥—Ä—É–ø–ø (noun chunks): {len(noun_chunks)}")
    else:
        print(f"- –ò–º–µ–Ω–Ω—ã–µ –≥—Ä—É–ø–ø—ã (noun chunks): –Ω–µ –ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞—é—Ç—Å—è –¥–ª—è —Ä—É—Å—Å–∫–æ–≥–æ —è–∑—ã–∫–∞")
    
    print(f"\n=== –ò–ú–ï–ù–û–í–ê–ù–ù–´–ï –°–£–©–ù–û–°–¢–ò ===")
    print(f"- –í—Å–µ–≥–æ —Å—É—â–Ω–æ—Å—Ç–µ–π: {len(entities)}")
    if entity_counts:
        print(f"- –¢–∏–ø—ã —Å—É—â–Ω–æ—Å—Ç–µ–π:")
        for label, count in entity_counts.most_common():
            print(f"  ‚Ä¢ {label}: {count}")
    
    print(f"\n=== –õ–ï–ö–°–ò–ß–ï–°–ö–û–ï –†–ê–ó–ù–û–û–ë–†–ê–ó–ò–ï ===")
    print(f"- TTR (—Å–æ–æ—Ç–Ω–æ—à–µ–Ω–∏–µ —Ç–∏–ø-—Ç–æ–∫–µ–Ω): {ttr:.3f}")
    print(f"- MTLD (—É–ø—Ä–æ—â–µ–Ω–Ω—ã–π): {mtld:.2f}")
    
    print(f"\n=== –°–¢–†–£–ö–¢–£–†–ê –¢–ï–ö–°–¢–ê ===")
    print(f"- –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏–π: {sentence_count}")
    print(f"- –°—Ä–µ–¥–Ω—è—è –¥–ª–∏–Ω–∞ –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏—è: {avg_sentence_length:.2f} —Ç–æ–∫–µ–Ω–æ–≤")
    print(f"- –í–æ–ø—Ä–æ—Å–∏—Ç–µ–ª—å–Ω—ã—Ö –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏–π: {question_count} ({question_count/sentence_count*100:.1f}% –æ—Ç –æ–±—â–µ–≥–æ)" if sentence_count else "- –í–æ–ø—Ä–æ—Å–∏—Ç–µ–ª—å–Ω—ã—Ö –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏–π: 0")
    print(f"- –í–æ—Å–∫–ª–∏—Ü–∞—Ç–µ–ª—å–Ω—ã—Ö –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏–π: {exclamation_count} ({exclamation_count/sentence_count*100:.1f}% –æ—Ç –æ–±—â–µ–≥–æ)" if sentence_count else "- –í–æ—Å–∫–ª–∏—Ü–∞—Ç–µ–ª—å–Ω—ã—Ö –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏–π: 0")
    
    print(f"\n=== –ß–ò–¢–ê–ë–ï–õ–¨–ù–û–°–¢–¨ ===")
    print(f"- –ò–Ω–¥–µ–∫—Å –§–ª–µ—à–∞-–ö–∏–Ω–∫–µ–π–¥–∞: {flesh_kincaid:.2f}")
    print(f"- –ü—Ä–æ—Ü–µ–Ω—Ç –¥–ª–∏–Ω–Ω—ã—Ö —Å–ª–æ–≤: {long_words_percent:.2f}%")
    
    print(f"\n=== –ß–ê–°–¢–û–¢–ù–´–ô –ê–ù–ê–õ–ò–ó ===")
    print(f"- –ù–∞–∏–±–æ–ª–µ–µ —á–∞—Å—Ç–æ—Ç–Ω—ã–µ —Å–ª–æ–≤–∞:")
    for word, count in most_common_words:
        print(f"  ‚Ä¢ {word}: {count}")
    
    if len(sentences) > 1:
        print(f"\n=== –°–ï–ú–ê–ù–¢–ò–ß–ï–°–ö–ê–Ø –°–í–Ø–ó–ù–û–°–¢–¨ ===")
        coherence_scores = []
        for i in range(len(sentences)-1):
            if len(sentences[i]) > 0 and len(sentences[i+1]) > 0:
                try:
                    if sentences[i].vector_norm > 0 and sentences[i+1].vector_norm > 0:
                        sim = sentences[i].similarity(sentences[i+1])
                        coherence_scores.append(sim)
                except:
                    pass
        
        if coherence_scores:
            avg_coherence = sum(coherence_scores) / len(coherence_scores)
            print(f"- –°—Ä–µ–¥–Ω—è—è —Å–µ–º–∞–Ω—Ç–∏—á–µ—Å–∫–∞—è —Å–≤—è–∑–Ω–æ—Å—Ç—å –º–µ–∂–¥—É –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏—è–º–∏: {avg_coherence:.3f}")

